<div data-type-section="{{$section}}" data-type-parent="{{$parent}}">
<a href="#/" data-type-section="{{$section}}" data-type-parent="{{$parent}}" onclick="createParagraph(this)" class="remove-field point-7-em-font">+Paragraph</a> &nbsp;|&nbsp;
<a href="#/" data-type-section="{{$section}}" data-type-parent="{{$parent}}"  onclick="removeParagraph(this)" class="remove-field point-7-em-font">-Paragraph</a> &nbsp;|&nbsp;
<a href="#/" data-type-section="{{$section}}" data-type-parent="{{$parent}}" onclick="createList(this)" class="remove-list point-7-em-font">+List</a> &nbsp;|&nbsp;
<a href="#/" data-type-section="{{$section}}" data-type-parent="{{$parent}}" onclick="createSubheading(this)" class="remove-subheading point-7-em-font">+Subheading</a> 
</div>